	delete d.children;
}
function expand(d){
	getData({name: d.name}, function(json){
		if(json && json.children){
			// 获取到此节点有子节点
			d._children = json.children;
			d.children = d._children.slice(0, limit);
			if(d._children.length > d.children.length){
				d.children.push({'name': 'more'});
