	if(d.name == 'more'){
		// 点击更多
		d.parent.children = d.parent._children.slice(0, (d.parent.children.length - 1) + limit);
		if(d.parent._children.length > d.parent.children.length){
			d.parent.children.push({'name': 'more'});
		}
